--
-- Table structure for table `wp_2_posts`
--

DROP TABLE IF EXISTS `wp_2_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_2_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=33 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_2_posts`
--

LOCK TABLES `wp_2_posts` WRITE;
/*!40000 ALTER TABLE `wp_2_posts` DISABLE KEYS */;
INSERT INTO `wp_2_posts` VALUES (1,2,'2016-03-20 19:38:18','2016-03-20 10:38:18','<a href=\"http://ardis.hyperion/public-lectures.info/\">public-lectures.info  のサイト</a> へようこそ。これは最初の投稿です。編集もしくは削除してブログを始めてください！','Hello world!','','publish','open','open','','hello-world','','','2016-03-20 19:38:18','2016-03-20 10:38:18','',0,'http://ardis.hyperion/public-lectures.info/fukuoka/?p=1',0,'post','',1),(2,2,'2016-03-20 19:38:18','2016-03-20 10:38:18','これはサンプルページです。同じ位置に固定され、(多くのテーマでは) サイトナビゲーションメニューに含まれるため、ブログ投稿とは異なります。サイト訪問者に対して自分のことを説明する自己紹介ページを作成するのが一般的です。たとえば以下のようなものになります。 \n\n<blockquote>はじめまして。昼間はバイク便のメッセンジャーとして働いていますが、俳優志望でもあります。これは僕のブログです。ロサンゼルスに住み、ジャックという名前のかわいい犬を飼っています。好きなものはピニャコラーダ (通り雨に濡れるのも) 。</blockquote>\n\nまたは、このようなものでもよいでしょう。\n\n<blockquote>XYZ 小道具株式会社は1971年の創立以来、高品質の小道具を皆様にご提供させていただいています。ゴッサム・シティに所在する当社では2,000名以上の社員が働いており、様々な形で地域のコミュニティへ貢献しています。</blockquote>\n\n新しく WordPress ユーザーになった方は、<a href=\"http://ardis.hyperion/public-lectures.info/fukuoka/wp-admin/\">ダッシュボード</a>へ行ってこのページを削除し、独自のコンテンツを含む新しいページを作成してください。それでは、お楽しみください !','サンプルページ','','trash','closed','open','','sample-page','','','2016-03-20 19:53:22','2016-03-20 10:53:22','',0,'http://ardis.hyperion/public-lectures.info/fukuoka/?page_id=2',0,'page','',0),(3,1,'2016-03-20 19:40:16','0000-00-00 00:00:00','','自動下書き','','auto-draft','open','open','','','','','2016-03-20 19:40:16','0000-00-00 00:00:00','',0,'http://ardis.hyperion/public-lectures.info/fukuoka/?p=3',0,'post','',0),(4,1,'2016-03-20 19:41:13','0000-00-00 00:00:00','','自動下書き','','auto-draft','open','open','','','','','2016-03-20 19:41:13','0000-00-00 00:00:00','',0,'http://ardis.hyperion/public-lectures.info/fukuoka/?p=4',0,'post','',0),(5,1,'2016-03-20 19:51:05','0000-00-00 00:00:00','','自動下書き','','auto-draft','open','open','','','','','2016-03-20 19:51:05','0000-00-00 00:00:00','',0,'http://ardis.hyperion/public-lectures.info/fukuoka/?p=5',0,'post','',0),(6,1,'2016-03-20 19:52:55','2016-03-20 10:52:55','<a href=\"http://www.nakamura-u.ac.jp/so